package com.example.newsdevappimport android.os.Bundleimport androidx.activity.ComponentActivityimport androidx.activity.compose.setContentimport androidx.navigation.compose.rememberNavControllerimport com.example.analytics.di.DaggerAnalyticsComponentimport com.example.feature.news.di.DaggerNewsComponentimport com.example.feature.search.di.DaggerSearchNewsComponentimport com.example.network.di.DaggerNetworkComponentimport com.example.newsdevapp.newsapp.NewsApplicationimport com.example.newsdevapp.ui.theme.NewsDevAppThemeclass MainActivity : ComponentActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        val appComponent = (applicationContext as NewsApplication).appComponent        val networkComponent = DaggerNetworkComponent.create()        val analyticsComponent = DaggerAnalyticsComponent.create()        val newsComponent = DaggerNewsComponent            .factory()            .createNetworkApi(                networkApi = networkComponent,                analyticsApi = analyticsComponent            )        val newsSearchComponent = DaggerSearchNewsComponent            .factory()            .createNetworkApi(                networkApi = networkComponent,                analyticsApi = analyticsComponent            )        appComponent.inject(this)        setContent {            NewsDevAppTheme {                val navController = rememberNavController()                NavHostNewDevApp(                    navController = navController,                    newsStore = newsComponent.getNewsStore(),                    searchNewsStore = newsSearchComponent.getSearchNewsStore()                )            }        }    }}